INSERT INTO `contents` VALUES ('49', '6', '2', null, 'Mụn mủ là do vùng da bị viêm có chứa mủ đầy mủ. Mủ là một sự kết hợp của vi khuẩn, bạch cầu và các tế bào da chết. Mụn mủ thường được hình thành xung quanh các nang bã nhờn và tóc.\r\n', '', '[\"ce7902ff2833c217727eb37fc88b0f33image-1png.png\"]', 'Định Nghĩa', '', null, '1', '2016-11-21 21:57:29', '2016-11-24 15:09:07', '2/5/1', '3', '1');
