INSERT INTO `contents` VALUES ('57', '7', '2', null, 'Là loại mụn viêm với đường kính to hơn rất nhiều so với Mụn Đỏ & Mụn Mủ. Triệu chứng sưng đỏ, gây đau nhức nhiều. Lúc này sự viêm nhiễm đã xâm nhập sâu dưới lớp tế bào da gây nên mụn bọc và cho dù có lành vẫn sẽ để lại sẹo lõm.\r\n', '', '[\"01515d1e3a0c1de7082f5432cf3cb4a7imagepng.png\"]', 'Định Nghĩa', '', null, '1', '2016-11-21 22:03:11', '2016-11-24 15:11:13', '2/6/1', '3', '1');
