INSERT INTO `contents` VALUES ('65', '8', '2', null, 'Mụn viêm (mụn nang) là tổn thương có hình dạng như viên nang và chứa mủ lỏng hoặc nửa lỏng tương tự được tìm thấy trong mụn mủ. Tuy nhiên, u nang lớn hơn mụn mủ và có thể ăn sâu vào bên trong da, để lại sẹo.\r\n', '', '[\"fdfe7d6bcfe08debf5be2fc3310f101eimage-1png.png\"]', 'Định Nghĩa', '', null, '1', '2016-11-21 22:09:05', '2016-11-24 15:12:57', '2/7/1', '3', '1');
