INSERT INTO `contents` VALUES ('146', '8', '1', null, 'Nguyên nhân gây bệnh rụng tóc, hói đầu bao gồm: yếu tố di truyền, DHT, mất cân bằng nội tiết, các yếu tố dinh dưỡng và tâm lý hoặc chăm sóc tóc, da đầu chưa đúng cách.\r\n', 'd8f71660dfff051c94d48b7f47b11bc9iconpng.png', '[]', 'Nguyên nhân', '', null, '1', '2016-11-23 18:57:06', '2016-11-23 18:57:06', '7/2', '2', '2');
INSERT INTO `contents` VALUES ('147', '8', '1', null, 'Là phương pháp sử dụng các huyết tương giàu tiểu cầu  (viết tắt: PRP) còn gọi là tế bào mô sinh học. Là một nguồn tập trung của tiểu cầu tự thân, PRP chứa yếu tố tăng trưởng khác nhau và các cytokine khác có thể kích thích phục hồi tóc tổn thương, kích thích mọc tóc và giúp tóc chắc khỏe tự nhiên.\r\n', 'a9a797a369aea73a3a544cbf0b160d01iconpng.png', '[]', 'Công nghệ Bio Cell (PRP) ', '', null, '1', '2016-11-23 18:57:53', '2016-11-23 18:57:53', '7/3', '2', '3');
INSERT INTO `contents` VALUES ('148', '8', '1', null, 'Là phương pháp sử dụng các nang tóc tự thân của chính khách hàng (được lấy từ vùng cho tóc, bóc tách thành các đơn vị nhỏ (FUE) có từ 3-4 nang tóc có khả năng sống sót cao để cấy ghép vào vùng da đầu bị mất tóc giúp phục hồi tóc hiệu quả cho khách hàng. \r\n', '9624d0430c6692ae6c4e67bebc7639f2iconpng.png', '[]', 'Cấy tóc tự thân', '', null, '1', '2016-11-23 18:58:34', '2016-11-23 18:58:34', '7/4', '2', '4');
INSERT INTO `contents` VALUES ('149', '8', '1', null, 'Là giải pháp phục hồi tóc hoàn toàn không phẫu thuật, chỉ sử dụng thiết bị cấy tóc chuyên dụng (bút cấy) để đưa các sợi tóc nhân tạo có cấu tạo đặc biệt, độ tương thích cao vào vùng mất tóc một cách chính xác, mang lại hiệu quả phục hồi tóc cao ngay sau khi thực hiện, đảm bảo an toàn tuyệt đối.\r\n', '8b20e9337022eaa095c61190452cf7a8iconpng.png', '[]', 'Cấy tóc sinh học Biofibre', '', null, '1', '2016-11-23 18:59:27', '2016-11-23 18:59:27', '7/5', '2', '5');
