INSERT INTO `contents` VALUES ('314', '61', '2', null, '- Béo, thừa cân, khó mặc đồ, khó vận động, gây tự ti\r\n- Có mỡ thừa tích tụ ở vùng bụng, mặt, chân tay, đùi, lưng, vai,..\r\n- Mỡ thừa kèm theo tình trạng da chảy xệ, rạn da\r\n- Đã áp dụng nhiều phương pháp giảm béo nhưng không có được kết quả ưng ý\r\n- Mong muốn của khách hàng là thực hiện nhanh chóng, 1 lần là giảm mỡ được', '', '[]', 'Phân vùng/ Đối tượng áp dụng', '', null, '1', '2016-11-24 16:33:06', '2016-11-24 16:33:06', '6/3/1', '3', '1');
