INSERT INTO `contents` VALUES ('320', '61', '2', null, '- Giảm mỡ bụng toàn thể (Bụng trên + Bụng dưới +\r\nVùng eo): 55 triệu\r\n- Giảm mỡ bụng (Trên + Dưới): 45 triệu\r\n- Giảm mỡ bụng (Trên/Dưới): 35 triệu\r\n- Giảm mỡ đùi: 60 triệu\r\n- Giảm mỡ bắp tay: 25 triệu\r\n- Giảm mỡ bắp chân: 40 triệu\r\n- Giảm mỡ nọng cằm/má/vai/lưng/mông: 25 triệu', '', '[]', 'Chi phí', '', null, '1', '2016-11-24 16:34:24', '2016-11-24 16:34:24', '6/3/7', '3', '7');
INSERT INTO `contents` VALUES ('321', '106', '2', null, '- Áp dụng cho mọi đối tượng, yêu cầu trắng nhanh trong khoảng thời gian ngắn - Da sạm đen, da thô ráp - Da cần đánh bật sáng màu', '', '[]', 'Đối tượng áp dụng?', '', null, '1', '2016-11-25 00:35:09', '2016-11-25 00:35:09', '5/2/1/1', '4', '1');
INSERT INTO `contents` VALUES ('322', '106', '2', null, '- Nguyên liệu dùng để tắm trắng White Plus gồm:\r\n+ Tinh chất Arbutin\r\n+ Tinh chất Collagen\r\n+ Cám gạo, Sữa dê non\r\n+ Ánh sáng đa năng (ánh sáng blue light)', '', '[]', 'Nguyên liệu tắm trắng', '', null, '1', '2016-11-25 00:35:23', '2016-11-25 00:35:23', '5/2/1/2', '4', '2');
INSERT INTO `contents` VALUES ('323', '106', '2', null, '- Arbutin: Giúp ức chế enzyme sản sinh ra melanin trong tế bào, đồng thời có tác dụng chống lão hoá, ngăn chặn các gốc tự do.\r\n- Collagen: chiếm khoảng 70% cấu trúc da và được phân bổ chủ yếu ở lớp hạ bì của da. Collagen có tác dụng kết nối các tế bào, kích thích quá trình trao đổi chất, tạo độ đàn hồi của da- Cám gạo:  Chứa các loại vitamin (E, B), chất béo omega 3, axit phytic,… hỗ trợ tẩy da chết nhẹ, đồng thời kiểm soát sắc tố da, tiêu hủy tế bào đen sạm.\r\n- Sữa dê non: Dưỡng ẩm và nuôi dưỡng tế bào da, cân bằng độ ẩm lý tưởng cho làn da từ trong ra ngoài- Ánh sáng đa năng:  Tự động nhận diện và tiêu hủy các tế bào da cũ, tái tạo da mới và giúp tinh chất trắng da thẩm thấu sâu vào bên trong cơ thể giúp tế bào khỏe mạnh tăng lưu thông máu đến nuôi dưỡng tế bào da và loại bỏ độc tố trên da.', '', '[]', 'Công dụng của từng loại nguyên liệu', '', null, '1', '2016-11-25 00:35:36', '2016-11-25 00:35:36', '5/2/1/3', '4', '3');
